package Basic_Program;

public class ReleationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println(10<20);
		System.out.println(10>20);
		System.out.println(10!=20);
		System.out.println(10==20);
		System.out.println(10>=20);
		System.out.println(10<=20);
		
		boolean c=1%3==8%7;
		System.out.println(c);
	}

}
